  async mounted() {
    this.entireJson = JSON.parse(this.originalText);
    this.wavesPlan = this.entireJson.waves_v2;
  },
  methods: {
    getLevelName(level) {
      return parseInt(level) + 1;
    },
    setSelectedLevelHandler(wave) {
      this.selectedLevel = wave;
      this.$forceUpdate();
    },
    deleteLevel(wave) {
      this.wavesPlan.splice(wave, 1);
      this.selectedLevel = 0;
    },
    getNumberOfZombies(level) {
      let zombieCount = 0;
      for (let i = 0; i < level.length; i++) {
        zombieCount += level[i][2];
      }
      return zombieCount;
    },
    duplicateLevel(wave) {
      let newKey = 0;
      const newObject = [];
      for (let i = 0; i < this.wavesPlan.length; i++) {
        newObject[newKey] = JSON.parse(JSON.stringify(this.wavesPlan[i]));
        if (i === wave) {
          newKey++;
          newObject[newKey] = JSON.parse(JSON.stringify(this.wavesPlan[i]));
        }
        newKey++;
      }
      this.wavesPlan = newObject;
      this.selectedLevel = wave + 1;
    },
    async importFile() {
      let fileHandle;
      [fileHandle] = await window.showOpenFilePicker();
      const file = await fileHandle.getFile();
      const content = await file.text();
      this.originalText = content;
      this.entireJson = JSON.parse(content);
      this.wavesPlan = JSON.parse(content).waves_v2;
      this.renderKey += 1;
    },
    async exportFile() {
      const handle = await window.showSaveFilePicker();
      const writable = await handle.createWritable();
      let st = this.myReplace(this.originalText, this.formatReplacementWave(this.wavesPlan));
      await writable.write(st);
      await writable.close();
    },
    copyToClipboard() {
      let selBox = document.createElement("textarea");
      selBox.style.position = "absolute";
      selBox.style.left = "0";
      selBox.style.top = "0";
      selBox.style.opacity = "0";
      let st = this.myReplace(this.originalText, this.formatReplacementWave(this.wavesPlan));
      selBox.value = st;
      document.body.appendChild(selBox);
      selBox.focus();
      selBox.select();
      document.execCommand("copy");
      document.body.removeChild(selBox);
      this.showDismissibleAlert = true;
    },
    myReplace(string, replacement) {
      replacement = replacement.substring(1, replacement.length - 1);
      var result = string.match(/(?<="waves_v2": ).*?(?=,\s+"settings")/gs);
      return string.replace(result, replacement);
    },
    formatReplacementWave(replacementWave) {
      let tmp = "";
      tmp += ' [\n';
      for (let i = 0; i < replacementWave.length; i++) {
        if (i == replacementWave.length - 1) {
          tmp += '        ' + JSON.stringify(replacementWave[i]) + '\n';
        } else {
          tmp += '        ' + JSON.stringify(replacementWave[i]) + ',\n';
        }
      }
      tmp += '    ],';
      return tmp;
    }
  },
};
